// src/components/Resources.js
import React from 'react';
import './resources.css';  // Make sure to create and import the Resources.css for styling

const Resources = () => {
    const articles = [
        {
            id: 1,
            title: "Understanding Anxiety",
            description: "Explore the causes and management strategies for anxiety.",
            imageUrl: "/images/anxiety.jpg",
            link: "https://example.com/anxiety"
        },
        {
            id: 2,
            title: "Dealing with Depression",
            description: "Comprehensive guide on coping with depression.",
            imageUrl: "/images/depression.jpg",
            link: "https://example.com/depression"
        },
        // Add more articles as needed
    ];

    return (
        <div className="resources-container">
            <div className="articles-container">
                {articles.map(article => (
                    <a key={article.id} href={article.link} className="article-card">
                        <img src={article.imageUrl} alt={article.title} />
                        <h3>{article.title}</h3>
                        <p>{article.description}</p>
                    </a>
                ))}
            </div>
            <div className="playlist-container">
                <iframe 
                    style={{borderRadius: '12px'}}
                    src="https://open.spotify.com/embed/playlist/37i9dQZF1DWXRqgorJj26U?utm_source=generator" 
                    width="100%" 
                    height="352" 
                    frameBorder="0" 
                    allowFullScreen="" 
                    allow="autoplay; clipboard-write; encrypted-media; fullscreen; picture-in-picture" 
                    loading="lazy">
                </iframe>
            </div>
        </div>
    );
};

export default Resources;
