import React, { useState } from 'react';

function MoodForm({ onMoodSubmit, onCancel, selectedDate }) {
    const [mood, setMood] = useState('');
    const [description, setDescription] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        if (mood && description) {
            onMoodSubmit({ mood, description, timeOfDay: new Date().toLocaleTimeString() });
            setMood(''); // Clear the form after submission
            setDescription('');
        } else {
            alert('Please fill out all fields');
        }
    };

    return (
        <form className="mood-form" onSubmit={handleSubmit}>
            <h3>Log Your Mood for {selectedDate ? selectedDate.toLocaleDateString() : 'today'}</h3> {/* Fallback in case selectedDate is undefined */}
            <div>
                <label>Mood:</label>
                <select value={mood} onChange={(e) => setMood(e.target.value)}>
                    <option value="">Select mood</option>
                    <option value="Calm">üòä Calm</option>
                    <option value="Anxious">üòü Anxious</option>
                    <option value="Happy">üòÅ Happy</option>
                    <option value="Sad">üò¢ Sad</option>
                    <option value="Excited">üòÉ Excited</option>
                    <option value="Angry">üò° Angry</option>
                </select>
            </div>

            <div>
                <label>Description:</label>
                <textarea
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    placeholder="Describe how you are feeling..."
                ></textarea>
            </div>

            <div className="form-actions">
                <button type="submit">Log Mood</button>
                <button type="button" onClick={onCancel}>Cancel</button>
            </div>
        </form>
    );
}

export default MoodForm;
