import React, { useState, useEffect } from 'react';
import resourcesData from '../data/DummyData'; // Import the dummy data
import './Resources.css'; // Import the styling for resources

const Resources = ({ userMood }) => {
  const [recommendedResources, setRecommendedResources] = useState([]);
  const [selectedFilter, setSelectedFilter] = useState('all');

  useEffect(() => {
    // Get the mood-based resources
    const moodResources = resourcesData.find((resource) => resource.mood === userMood);
    if (moodResources) {
      setRecommendedResources(moodResources.resources);
    } else {
      // Default to 'calm' resources if no mood is matched
      const calmResources = resourcesData.find((resource) => resource.mood === 'calm');
      setRecommendedResources(calmResources.resources);
    }
  }, [userMood]);

  const handleFilterClick = (filter) => {
    setSelectedFilter(filter);
  };

  return (
    <div className="resources-page">
      <h1>Recommended Resources for You</h1>

      {/* Filter Section */}
      <div className="filter-section">
        <button 
          className={selectedFilter === 'anxiety' ? 'filter-btn active' : 'filter-btn'}
          onClick={() => handleFilterClick('anxiety')}
        >
          Managing Anxiety
        </button>
        <button 
          className={selectedFilter === 'food' ? 'filter-btn active' : 'filter-btn'}
          onClick={() => handleFilterClick('food')}
        >
          Managing Food
        </button>
        <button 
          className={selectedFilter === 'life' ? 'filter-btn active' : 'filter-btn'}
          onClick={() => handleFilterClick('life')}
        >
          Managing Life
        </button>
      </div>

      {/* Resource Section */}
      <div className="content-section">
        <div className="resources-section">
          {recommendedResources.map((resource, index) => (
            <div className="resource-card" key={index}>
              <h3>{resource.title}</h3>
              <p>{resource.description}</p>
              <a href={resource.link} className="read-more">Read more</a>
            </div>
          ))}
        </div>

        {/* Spotify Playlist */}
        <div className="spotify-section">
          <iframe
            style={{ borderRadius: '12px' }}
            src="https://open.spotify.com/embed/playlist/37i9dQZF1DWXRqgorJj26U?utm_source=generator"
            width="100%" height="352" frameBorder="0"
            allow="autoplay; clipboard-write; encrypted-media; fullscreen; picture-in-picture"
            loading="lazy"
          ></iframe>
        </div>
      </div>
    </div>
  );
};

export default Resources;
