import React, { useState } from 'react';
import './Resources.css'; // Assuming you have a CSS file for styling

const SadRecommendations = () => {
    const [activeFilter, setActiveFilter] = useState('sad');

    const recommendations = {
        sad: [
            { title: 'Overcoming Sadness', content: 'Strategies to deal with sad feelings.' },
            { title: 'Supportive Communities', content: 'Find groups that can help uplift your spirits.' }
        ],
        other: [
            { title: 'Managing Anxiety', content: 'Learn coping strategies for anxiety.' },
            { title: 'Boost Your Mood', content: 'Activities to quickly uplift your mood.' }
        ]
    };

    const handleFilterChange = (filter) => {
        setActiveFilter(filter);
    };

    return (
        <div className="recommendations-page">
            <h1>Recommendations for You</h1>
            <div className="filters">
                <button
                    className={activeFilter === 'sad' ? 'active' : ''}
                    onClick={() => handleFilterChange('sad')}
                >
                    Sad
                </button>
                <button
                    className={activeFilter === 'other' ? 'active' : ''}
                    onClick={() => handleFilterChange('other')}
                >
                    Other Moods
                </button>
            </div>
            <div className="recommendations-section">
                {recommendations[activeFilter].map((recommendation, index) => (
                    <div className="recommendation-card" key={index}>
                        <h2>{recommendation.title}</h2>
                        <p>{recommendation.content}</p>
                    </div>
                ))}
            </div>
            <div className="spotify-section">
                <iframe
                    title="Relaxing Playlist"
                    src="https://open.spotify.com/embed/playlist/37i9dQZF1DWXRqgorJj26U?utm_source=generator"
                    width="100%" height="380"
                    frameBorder="0"
                    allowFullScreen=""
                    allow="autoplay; clipboard-write; encrypted-media; fullscreen; picture-in-picture"
                    loading="lazy">
                </iframe>
            </div>
        </div>
    );
};

export default SadRecommendations;
