import React, { useState, useEffect } from 'react';
import axios from 'axios';
import MoodForm from './MoodForm';
import MoodList from './MoodList';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

function MoodTracker() {
    const [selectedDate, setSelectedDate] = useState(new Date());
    const [moods, setMoods] = useState([]);
    const [showForm, setShowForm] = useState(false); // Manage form visibility

    // Fetch moods for the selected date
    useEffect(() => {
        const fetchMoods = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/api/moods/${selectedDate.toISOString().split('T')[0]}`);
                setMoods(response.data);
            } catch (error) {
                console.error(error);
            }
        };

        fetchMoods();
    }, [selectedDate]);

    // Handle form submission for mood logging
    const handleMoodSubmit = async (newMood) => {
        try {
            await axios.post('http://localhost:5000/api/moods', { ...newMood, date: selectedDate.toISOString().split('T')[0] });
            // Fetch updated moods after the new mood is logged
            const updatedMoods = await axios.get(`http://localhost:5000/api/moods/${selectedDate.toISOString().split('T')[0]}`);
            setMoods(updatedMoods.data); // Update the moods in the UI
            setShowForm(false); // Close the form after submission
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <div className="mood-tracker-container">
            {/* Left Section: Calendar */}
            <div className="date-picker-container">
                <DatePicker
                    selected={selectedDate}
                    onChange={(date) => setSelectedDate(date)}
                    inline
                    maxDate={new Date()}  // Disable future dates
                />
                {/* Toggle Form Visibility */}
                {!showForm && (
                    <button onClick={() => setShowForm(true)}>
                        Log New Mood
                    </button>
                )}
            </div>
            
            {/* Right Section: Mood Display */}
            <div className="mood-list">
                {moods.length > 0 ? (
                    <MoodList moods={moods} />
                ) : (
                    <p>No mood logged for this day</p>
                )}

                {/* Show the form when "Log New Mood" is clicked */}
                {showForm && (
                    <MoodForm
                        onMoodSubmit={handleMoodSubmit}
                        onCancel={() => setShowForm(false)} // Hide form when cancelled
                        selectedDate={selectedDate}  // Pass the selectedDate prop
                    />
                )}
            </div>
        </div>
    );
}

export default MoodTracker;
