// src/components/Profile.js
import React, { useState } from 'react';
import './Profile.css';  // Your CSS styles for the profile page

// Data arrays for experiences, interests, and looking for options
const experiencesList = [
  'Abuse', 'Addiction', 'Anger', 'Anxiety', 'Benefits', 'Bereavement', 'Bipolar disorder', 'Body dysmorphia', 'Caring for someone',
  'Depression', 'Discrimination', 'Dissociative Disorders', 'Eating problems', 'Exercise Problems', 'Feeling low',
  'Hypomania and Mania', 'Hearing voices', 'Health Anxiety', 'Hoarding', 'Loneliness', 'Medication', 'Menopause and peri-menopause',
  'Neurodivergence', 'Not sure right now', 'OCD', 'Panic attacks', 'Paranoia', 'Personality Disorders', 'Perinatal depression',
  'Perinatal Mental Health', 'Phobias', 'Physical Health', 'PMDD', 'PTSD/C-PTSD', 'Psychosis', 'Schizophrenia', 'Stress',
  'Self-Esteem', 'Self-Harm', 'Sleep Problems', 'Social Anxiety', 'Suicidal Feelings', 'Substance Abuse', 'Schizoaffective Disorder',
  'Trauma'
];

const interestsList = [
  'Animals', 'Arts and Culture', 'Books', 'Computers or Technology', 'Cooking and Baking', 'Creativity', 'Fashion', 'Gaming',
  'Gardening', 'Mindfulness and Meditation', 'Outdoors', 'Parenting', 'Photography', 'Reading', 'Self-care', 'Sports', 'Travel', 'Writing'
];

const lookingForList = [
  'Looking for support', 'Sharing a little win', 'Seeking suggestions', 'Saying hello', 'Just Venting', 'My friends'
];

const Profile = () => {
  const [user, setUser] = useState({
    username: 'susha101',
    joined: '6 hours ago',
    story: 'Add your story so other members can get a better idea of who you are and why youâ€™re here.',
    interests: ['Creativity', 'Cooking and Baking'],  // Pre-selected interests
    experiences: ['Trauma'],  // Pre-selected experiences
    lookingFor: ['Looking for support']  // Pre-selected 'looking for' options
  });

  const [editingSection, setEditingSection] = useState(null);
  const [selectedExperiences, setSelectedExperiences] = useState(user.experiences);
  const [selectedInterests, setSelectedInterests] = useState(user.interests);
  const [selectedLookingFor, setSelectedLookingFor] = useState(user.lookingFor);

  const handleEdit = (section) => {
    setEditingSection(section);
  };

  const handleSave = async (section) => {
    try {
      // API call logic can be adjusted based on which section is being saved
      let response;
      if (section === 'experiences') {
        response = await fetch('/api/profile/experiences', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ experiences: selectedExperiences }),
        });
        if (response.ok) {
          setUser({ ...user, experiences: selectedExperiences });
        }
      } else if (section === 'interests') {
        response = await fetch('/api/profile/interests', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ interests: selectedInterests }),
        });
        if (response.ok) {
          setUser({ ...user, interests: selectedInterests });
        }
      } else if (section === 'lookingFor') {
        response = await fetch('/api/profile/looking-for', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ lookingFor: selectedLookingFor }),
        });
        if (response.ok) {
          setUser({ ...user, lookingFor: selectedLookingFor });
        }
      }

      setEditingSection(null); // Exit edit mode
    } catch (error) {
      console.error('Error saving data:', error);
    }
  };

  const handleCancel = () => {
    setSelectedExperiences(user.experiences);  // Reset the selected experiences
    setSelectedInterests(user.interests);  // Reset the selected interests
    setSelectedLookingFor(user.lookingFor);  // Reset the selected looking for options
    setEditingSection(null);
  };

  // Function to handle selection toggling for experiences, interests, or lookingFor
  const handleSelectionClick = (item, setSelectedItems, selectedItems) => {
    if (selectedItems.includes(item)) {
      setSelectedItems(selectedItems.filter(i => i !== item));
    } else {
      setSelectedItems([...selectedItems, item]);
    }
  };

  return (
    <div className="profile-page">
      <div className="profile-header">
        <h1>{user.username}</h1>
      </div>

      {/* User Story Section */}
      <div className="profile-section">
        <h2>My story</h2>
        <p>{user.story}</p>
        <button className="edit-btn" onClick={() => handleEdit('story')}>Edit</button>
      </div>

      {/* Interests Section */}
      <div className="profile-section">
        <h2>My interests</h2>
        {editingSection === 'interests' ? (
          <>
            <div className="interests-container">
              {interestsList.map((interest) => (
                <button
                  key={interest}
                  className={selectedInterests.includes(interest) ? 'tag selected' : 'tag'}
                  onClick={() => handleSelectionClick(interest, setSelectedInterests, selectedInterests)}
                >
                  {interest}
                </button>
              ))}
            </div>
            <div className="edit-actions">
              <button className="cancel-btn" onClick={handleCancel}>Cancel</button>
              <button className="save-btn" onClick={() => handleSave('interests')}>Save</button>
            </div>
          </>
        ) : (
          <>
            <div className="selected-tags">
              {user.interests.map((interest) => (
                <span key={interest} className="tag selected">{interest}</span>
              ))}
            </div>
            <button className="edit-btn" onClick={() => handleEdit('interests')}>Edit</button>
          </>
        )}
      </div>

      {/* Experiences Section */}
      <div className="profile-section">
        <h2>My experiences</h2>
        {editingSection === 'experiences' ? (
          <>
            <div className="experiences-container">
              {experiencesList.map((experience) => (
                <button
                  key={experience}
                  className={selectedExperiences.includes(experience) ? 'tag selected' : 'tag'}
                  onClick={() => handleSelectionClick(experience, setSelectedExperiences, selectedExperiences)}
                >
                  {experience}
                </button>
              ))}
            </div>
            <div className="edit-actions">
              <button className="cancel-btn" onClick={handleCancel}>Cancel</button>
              <button className="save-btn" onClick={() => handleSave('experiences')}>Save</button>
            </div>
          </>
        ) : (
          <>
            <div className="selected-tags">
              {user.experiences.map((exp) => (
                <span key={exp} className="tag selected">{exp}</span>
              ))}
            </div>
            <button className="edit-btn" onClick={() => handleEdit('experiences')}>Edit</button>
          </>
        )}
      </div>

      {/* What You Are Looking For Section */}
      <div className="profile-section">
        <h2>What I'm looking for</h2>
        {editingSection === 'lookingFor' ? (
          <>
            <div className="looking-for-container">
              {lookingForList.map((item) => (
                <button
                  key={item}
                  className={selectedLookingFor.includes(item) ? 'tag selected' : 'tag'}
                  onClick={() => handleSelectionClick(item, setSelectedLookingFor, selectedLookingFor)}
                >
                  {item}
                </button>
              ))}
            </div>
            <div className="edit-actions">
              <button className="cancel-btn" onClick={handleCancel}>Cancel</button>
              <button className="save-btn" onClick={() => handleSave('lookingFor')}>Save</button>
            </div>
          </>
        ) : (
          <>
            <div className="selected-tags">
              {user.lookingFor.map((item) => (
                <span key={item} className="tag selected">{item}</span>
              ))}
            </div>
            <button className="edit-btn" onClick={() => handleEdit('lookingFor')}>Edit</button>
          </>
        )}
      </div>
    </div>
  );
};

export default Profile;
