import React, { useState } from 'react';
import './Profile.css'; // Make sure your CSS file contains the necessary styles

const Profile = () => {
  const [user, setUser] = useState({
    username: "susha101",
    joined: "13 hours ago",
    interests: ["Computers or Technology"],
    experiences: ["Bipolar disorder", "Trauma"],
    avatar: "" // This will hold the base64 encoded image
  });

  const handleImageChange = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();
    reader.onloadend = () => {
      setUser({...user, avatar: reader.result});
    };
    reader.readAsDataURL(file);
  };

  return (
    <div className="profile-container">
      <div className="profile-header">
        <div className="profile-avatar" style={{ backgroundImage: `url(${user.avatar || '/default-avatar.png'})` }}>
          <label className="change-picture-btn">Change Picture
            <input type="file" onChange={handleImageChange} style={{ display: 'none' }} />
          </label>
        </div>
        <div className="profile-details">
          <h1>{user.username}</h1>
          <p>Joined {user.joined}</p>
        </div>
      </div>

      {/* Interests Section */}
      <div className="profile-section">
        <h2>My Interests</h2>
        <div>
          {user.interests.map((interest, index) => (
            <span key={index} className="profile-tag">{interest}</span>
          ))}
        </div>
      </div>

      {/* Experiences Section */}
      <div className="profile-section">
        <h2>My Experiences</h2>
        <div>
          {user.experiences.map((exp, index) => (
            <span key={index} className="profile-tag">{exp}</span>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Profile;
