import React, { useState, useEffect } from 'react';
import axios from 'axios';
import MoodForm from './MoodForm';
import MoodList from './MoodList';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

function MoodTracker() {
    const [selectedDate, setSelectedDate] = useState(new Date());
    const [moods, setMoods] = useState([]);
    const [isTodayMoodLogged, setIsTodayMoodLogged] = useState(false);
    const [isLoggingAllowed, setIsLoggingAllowed] = useState(false);

    const isToday = (date) => {
        const today = new Date();
        return date.getDate() === today.getDate() &&
            date.getMonth() === today.getMonth() &&
            date.getFullYear() === today.getFullYear();
    };

    const isPastOrToday = (date) => {
        const today = new Date();
        return date <= today;
    };

    useEffect(() => {
        const fetchMoods = async () => {
            if (isPastOrToday(selectedDate)) {
                try {
                    const response = await axios.get(`http://localhost:5000/api/moods/${selectedDate.toISOString().split('T')[0]}`);
                    setMoods(response.data);
                    setIsTodayMoodLogged(response.data.length > 0 && isToday(selectedDate));
                } catch (error) {
                    console.error(error);
                }
            }
        };

        // Determine if logging is allowed for the selected date (only today)
        setIsLoggingAllowed(isToday(selectedDate));

        fetchMoods();
    }, [selectedDate]);

    const handleMoodSubmit = async (newMood) => {
        if (!isToday(selectedDate)) {
            alert("You can only log a mood for today.");
            return;
        }

        try {
            await axios.post('http://localhost:5000/api/moods', { ...newMood, date: selectedDate });
            setMoods([...moods, newMood]);
            setIsTodayMoodLogged(true);
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <div className="app-container">
            <h1>Mood Tracker</h1>
            <div className="date-picker-container">
                <DatePicker
                    selected={selectedDate}
                    onChange={(date) => setSelectedDate(date)}
                    inline
                    maxDate={new Date()}  // Disable future dates
                />
                <button
                    onClick={() => setIsTodayMoodLogged(false)}
                    disabled={!isLoggingAllowed}  // Disable button if it's not today
                    className={!isLoggingAllowed ? "disabled-button" : ""}
                >
                    Log New Mood
                </button>
            </div>
            <div className="mood-list">
                {moods.length > 0 ? (
                    <MoodList moods={moods} />
                ) : (
                    isLoggingAllowed && !isTodayMoodLogged && <MoodForm onMoodSubmit={handleMoodSubmit} />
                )}
            </div>
        </div>
    );
}

export default MoodTracker;
