// src/components/Resources.js
import React, { useState } from 'react';
import articles from '../data/dummyData';  // Adjust the path according to your project structure
import './resources.css';

const Resources = () => {
    const [activeCategory, setActiveCategory] = useState('all');

    const handleFilterChange = (category) => {
        setActiveCategory(category);
    };

    const filteredArticles = articles.filter(article => 
        activeCategory === 'all' || article.category === activeCategory
    );

    return (
        <div className="resources-container">
            <div className="filters">
                <button onClick={() => handleFilterChange('all')}>All</button>
                <button onClick={() => handleFilterChange('managing anxiety')}>Managing Anxiety</button>
                <button onClick={() => handleFilterChange('managing food')}>Managing Food</button>
                <button onClick={() => handleFilterChange('managing life')}>Managing Life</button>
            </div>
            <div className="articles-container">
                {filteredArticles.map(article => (
                    <div key={article.id} className="article-card">
                        <img src={article.imageUrl} alt={article.title} />
                        <h3>{article.title}</h3>
                        <p>{article.description}</p>
                        <a href={article.link} className="read-more">Read more</a>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Resources;
