import React, { useState, useEffect } from 'react';
import axios from 'axios';
import MoodForm from './MoodForm';
import MoodList from './MoodList';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

function MoodTracker() {
    const [selectedDate, setSelectedDate] = useState(new Date());
    const [moods, setMoods] = useState([]);

    useEffect(() => {
        const fetchMoods = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/api/moods/${selectedDate.toISOString().split('T')[0]}`);
                setMoods(response.data);
            } catch (error) {
                console.error(error);
            }
        };

        fetchMoods();
    }, [selectedDate]);

    const handleMoodSubmit = async (newMood) => {
        try {
            await axios.post('http://localhost:5000/api/moods', { ...newMood, date: selectedDate });
            setMoods([...moods, newMood]);
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <div className="mood-tracker-container">
            {/* Left Section: Calendar */}
            <div className="date-picker-container">
                <DatePicker
                    selected={selectedDate}
                    onChange={(date) => setSelectedDate(date)}
                    inline
                    maxDate={new Date()}  // Disable future dates
                />
                {/* The button is now always visible */}
                <button onClick={() => setIsTodayMoodLogged(false)}>
                    Log New Mood
                </button>
            </div>
            
            {/* Right Section: Mood Display */}
            <div className="mood-list">
                {moods.length > 0 ? (
                    <MoodList moods={moods} />
                ) : (
                    <p>No mood logged for this day</p>
                )}
            </div>
        </div>
    );
}

export default MoodTracker;
